import React, { useState } from "react";
import defaultProfile from "../../image/profile.png";

export default function ProfileBox() {
  const [profileImage, setProfileImage] = useState(defaultProfile);

  const handleImageChange = (e) => {
    if (e.target.files && e.target.files[0]) {
      setProfileImage(URL.createObjectURL(e.target.files[0]));
    }
  };

  return (
    <div className="profile-container flex flex-col items-center justify-center">
      <img id="profile-img" src={profileImage} alt="profile" />
      <input type="file" onChange={handleImageChange} />
      <p className="name">Wish</p>
      <p className="email">wish.eq@gmail.com</p>
    </div>
  );
}
