import React, { Component } from "react";

export class ProfileBox extends Component {
  constructor(props) {
    super(props);
    this.state = {
      image: null,
    };
  }

  handleImageChange = (e) => {
    const file = e.target.files[0];

    if (file) {
      const reader = new FileReader();

      reader.onload = (e) => {
        this.setState({
          image: e.target.result,
        });
      };

      reader.readAsDataURL(file);
    }
  };

  render() {
    return (
      <div className="profile-container flex flex-col items-center justify-center">
        <input type="file" accept="image/*" onChange={this.handleImageChange} />
        {this.state.image && (
          <img id="profile-img" src={this.state.image} alt="profile" />
        )}
        <p className="name">Wish</p>
        <p className="email">wish.eq@gmail.com</p>
      </div>
    );
  }
}

export default ProfileBox;
